import React, { useState } from "react";
import { ReactComponent as UploadIcon } from "../../assets/images/cloud-upload.svg";
import "./DropBox.scss";

export default function DropBox() {
  const [selectFiles, setSelectFiles] = useState([]);
  const [errorMessage, setErrorMessage] = useState("");

  const dragOver = (e) => {
    e.preventDefault();
  };

  const dragEnter = (e) => {
    e.preventDefault();
  };

  const dragLeave = (e) => {
    e.preventDefault();
  };

  const handleFiles = (files) => {
    for (let i = 0; i < files.length; i++) {
      if (validateFile(files[i])) {
        setSelectedFiles((prevArray) => [...prevArray, files[i]]);
      } else {
        files[i]["invalid"] = true;
        setSelectedFiles((prevArray) => [...prevArray, files[i]]);
        setErrorMessage("File type not permitted");
        setUnsupportedFiles((prevArray) => [...prevArray, files[i]]);
      }
    }
  };

  const fileDrop = (e) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length) {
      handleFiles(files);
    }
  };

  const validateFile = (file) => {
    const validTypes = [
      "image/jpeg",
      "image/jpg",
      "image/png",
      "image/gif",
      "image/x-icon",
    ];
    if (validTypes.indexOf(file.type) === -1) {
      return false;
    }
    return true;
  };

  const fileSize = (size) => {
    if (size === 0) {
      return "0 Bytes";
    }
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB", "GB", "TB"];
    const i = Math.floor(Math.log(size) / Math.log(k));
    return parseFloat((size / Math.pow(k, i)).toFixed(2)) + " " + sizes[i];
  };

  const fileType = (fileName) => {
    return (
      fileName.substring(fileName.lastIndexOf(".") + 1, fileName.length) ||
      fileName
    );
  };

  return (
    <div className="DropBox">
      <div
        className="DropBox__container"
        onDragOver={dragOver}
        onDragEnter={dragEnter}
        onDragLeave={dragLeave}
        onDrop={fileDrop}
      >
        <div className="DropBox__container-message">
          <UploadIcon className="DropBox__container-message-icon" />
          <p>Drag & Drop files here or click to upload</p>
        </div>
      </div>
      <div>
        <div className="DropBox__file">
          <div className="DropBox__file-status">
            <div>
              <div className="DropBox__file-type">png</div>
              <span className="DropBox__file-name">test-file.png</span>
              <span className="DropBox__file-size">(20.5 KB)</span>{" "}
              {
                <span className="DropBox__file-error-message">
                  (File type not permitted)
                </span>
              }
            </div>
            <div className="DropBox__file-remove">X</div>
          </div>
        </div>
      </div>
    </div>
  );
}
