import React, { useState } from "react";
import { ReactComponent as Logo } from "../../assets/images/cloud-upload.svg";
import axios from "axios";
import "./FilesUploader.scss";

export default function FilesUploader() {
  const [uploads, setUploads] = useState([]);

  const onFileUpload = (e) => {
    setUploads(e.target.files);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = new FormData();

    for (let i = 0; i < uploads.length; i++) {
      data.append("file", uploads[i]);
    }

    axios
      .post("//localhost:8000/upload", data)
      .then((e) => {
        console.log("Success");
      })
      .catch((err) => {
        console.log("Error", err);
      });
  };

  return (
    <div className="FilesUploader">
      <form method="post" action="#" id="#" onSubmit={handleSubmit}>
        <div className="FilesUploader_form">
          <Logo className="FilesUploader_form-icon" />
          <label>Upload your files</label>
          <input
            type="file"
            onChange={onFileUpload}
            className="FilesUploader_form-control"
            multiple
          />
        </div>
        <button>Submit</button>
      </form>
    </div>
  );
}
